import Link from 'next/link'
import React, { useState } from "react";
import axios from "axios"

function Home() {

  const [temperature, setTemperature] = useState("");
  const [forecast, setForecast] = useState("");
  const [pic, setPic] = useState("");

  var d = new Date();

  const phillyWoeId = 2471217;
  var month = d.getMonth() + 1;
  var day = d.getDate();
  var year = d.getFullYear();

  var endURL = ""+ phillyWoeId +"/"+ year +"/"+ month+"/"+ day+"/";

  axios.get("https://www.metaweather.com/api/location/" + endURL).then((responseGet)=>{
    console.log(responseGet);
    setTemperature(responseGet.data[36].the_temp * (9.0/5) + 32.0);
    setForecast(responseGet.data[36].weather_state_name.toLowerCase());
    if(responseGet.data[36].weather_state_name.toLowerCase().includes("cloud")){
      setPic("https://lh3.googleusercontent.com/proxy/hk9PgLyLqgM0Me1vj-EsWfAlDk2O1AYLqj1hiG-77aH-lB7oVzCUCRTgtI5FUmUG-ZURb3Y4nU3PC4cky41uuQQgj0dRNn-WNpuyXOOUkJoyaEjbX7Ne");
    }else if(responseGet.data[36].weather_state_name.toLowerCase().includes("rain")){
      setPic("data:image/png;base64,");
    }
  });
  // axios({
  //   "method": "GET",
  //   "url": "https://weatherbit-v1-mashape.p.rapidapi.com/current",
  //   "headers": {
  //     "content-type": "application/octet-stream",
  //     "x-rapidapi-host": "weatherbit-v1-mashape.p.rapidapi.com",
  //     "x-rapidapi-key": "830f214fe3msh7fa9a15365fbc2bp14cb63jsn2a8b9cccfc0b"
  //   }, "params": {

  //     "units": "I",
  //     "lang": "undefined",
  //     "lon": "-75.165222",
  //     "lat": "39.9526"
  //   }
  // }).then((responseGet)=>{
  //   setTemperature(responseGet.data.data[0].temp);
  //   setForecast(responseGet.data.data[0].weather.description.toLowerCase());
  //   if(responseGet.data.data[0].weather.description.toLowerCase().includes("cloud")){
  //     setPic("https://lh3.googleusercontent.com/proxy/hk9PgLyLqgM0Me1vj-EsWfAlDk2O1AYLqj1hiG-77aH-lB7oVzCUCRTgtI5FUmUG-ZURb3Y4nU3PC4cky41uuQQgj0dRNn-WNpuyXOOUkJoyaEjbX7Ne");
  //   }
  // });

  var d = new Date();
  var n = d.getHours();
  var greeting = "";

  if (n < 12) {
    greeting = "Good morning";
  } else if (n < 17) {
    greeting = "Good afternoon";
  } else {
    greeting = "Good evening";
  }

  return (

    <div style={{ paddingLeft: '8px' }}>
      <link rel="stylesheet" href="//cdn.jsdelivr.net/npm/semantic-ui@2.4.2/dist/semantic.min.css" />
      <h1 class="ui header">WelcomeIN</h1>
      <div class="ui inverted menu">
        <a class="active item">
          Home
        </a>
        <Link href="/users">
          <a className="item">
            Authroized Individuals
          </a>
        </Link>
        <Link href="/settings">
          <a className="item">
            Notification Settings            </a>
        </Link>
        <Link href="/footage">
          <a className="item">
            Footage
      </a>
        </Link>

      </div>
      <div>
        <img style={{ float: 'left', width: '60%' }} src="https://equalrightscenter.org/wp-content/uploads/house-icon-1.png"></img>
        <div style={{ float: 'left' }}>
          <h2>{greeting}, Charlie!</h2>
          <div style={{ marginLeft: '40px' }}>
            <div>
              <p>The forecast is {(forecast == null) ? <em>Loading...</em> : forecast}.</p>
              <p>The temperature is {(temperature== null) ? <em>Loading...</em> : temperature} degrees.</p>
              <img style={{ width: '60%' }} src={pic}></img>
            </div>
          </div>
        </div>
      </div>
    </div>
  )

}


export default Home

